package pattern;

public class SwapInte {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 15;
		System.out.println("A = "+a+" B = "+b);
		a=a+b;//5+7=12
		b=a-b;//12-7=5
		a=a-b;//12-5=7
		System.out.println("A = "+a+" B = "+b);
	}
}
